#!/usr/bin/env python3

lines = [
    '02RETORNO01COBRANCA       00000000000000000000EMPRESA TESTE LTDA               341BANCO ITAU SA         ',
    '10212345601000000123456000000000000012345678900000000 ',
    ''
]

with open('examples/cnab400_valido.txt', 'w') as f:
    for i, line in enumerate(lines, 1):
        if len(line) < 400:
            line = line + ' ' * (400 - len(line))
        elif len(line) > 400:
            line = line[:400]
        f.write(line + '\n')
        print(f'Linha {i}: {len(line)} caracteres')

print('Arquivo cnab400_valido.txt criado com sucesso!') 